$(function(){$("#submitMail").click(function(e){e.preventDefault();let name=$("#name").val();let email=$("#email").val();let message=$("#message").val();$.ajax({url:"assets/contact/submit.php",type:"POST",data:{name:name,email:email,message:message},cache:!1,success:function(data){$('.required-message').html(data)},error:function(){$('.required-message').html("Oops. Something has gone wrong. <br> Please send me an email at <a href='mailTo:contact@matthewsullivan.media'>contact@matthewsullivan.media</a>.")}})})})